mdq:check.code:
#Check first if attribute are present.
if (!attributesPresent){
  status = "FAILURE"
  message <-"No attributes were found so unable to check attributes definitions."
} else{
  short = c()
  j=0
  # If only a single value is returned (vs type "list"), then convert to a list
  # for easier processing
  if(length(AttributesDefinitions)==1){
    AttributesDefinitions = list(AttributesDefinitions)
  }

  if(length(attributeName)==1){
    attributeName = list(attributeName)
  } 
  for (i in 1:length(AttributesDefinitions)){
    name = attributeName[i]
    if(is.null(name)){
      name=i
    }
    if(length(unlist(strsplit(AttributesDefinitions[i], "\\s+", perl=T)))<3){
      short=append(short,name)
    }
  }
  if(length(short) == 1){
    message = paste0("This attribute (out of ",length(AttributesDefinitions),") have a definition smaller than 3 words: ", short[1])
    status = "WARNING"

  }else if(length(short) > 1 & length(short) < 5){
    message = paste0("These ",length(short)," attributes (out of ",length(AttributesDefinitions),") have definitions of less than 3 words: ", paste(short,collapse=","))
    status="WARNING"
  }else if (length(short) > 5){
    message = paste0("These ",length(short)," attributes (out of ",length(AttributesDefinitions),") have definitions of less than 3 words: ", paste(short[1:5],collapse=",")," and ",length(short)-5," other(s)")
    status="WARNING"
  }else{
    message = "All definitions are more than 3 words long."
    status = "SUCCESS"
  }
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
