mdq:check.code:
if(length(RessourceLicense)<=0) {
  status <- "FAILURE"
  message <-"A ressource license is missing"
} else {
  status <- "SUCCESS"
  message <-paste0("The resource license '",substr(RessourceLicense,1,50),"...' was found.")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
